var pt = new SH_EmpTime();



var userId = gs.getUserID();
var weekStart = current.week_starts_on;
var sun = 0;
var mon = current.monday;
var tues = current.tuesday;
var wed = current.wednesday;
var thurs = current.thursday;
var fri = current.friday;
var sat = 0;
var category = current.pto_request_type;
var total = sun + mon + tues + wed + thurs + fri + sat;

pt.addTimeCard(userId,weekStart,sun,mon,tues,wed,thurs,fri,sat,category,total);

