var userId = gs.getUserID();
var ptoId = '';
var pt = new SH_EmpTime();
if(current.pto_request_type == 'excused_leave'){
	ptoId = 'Excused Leave';
	//gs.addInfoMessage(ptoId);
}
if(current.pto_request_type == 'funeral'){
	ptoId = 'Funeral';
	//gs.addInfoMessage(ptoId);
}
if(current.pto_request_type == 'sick_leave'){
	ptoId = 'Sick Leave';
	//gs.addInfoMessage(ptoId);
}

var weekStart = current.week_starts_on;
var sun = current.sunday;
var mon = current.monday;
var tues = current.tuesday;
var wed = current.wednesday;
var thurs = current.thursday;
var fri = current.friday;
var sat = current.saturday;
var category = current.pto_request_type;
var ws = current.work_start;
var we = current.work_end;
var total = sun + mon + tues + wed + thurs + fri + sat;

var taskId = pt.addNonPTOTimeCard(userId,weekStart,sun,mon,tues,wed,thurs,fri,sat,category,total);

var cal = new SH_PTOCalendar();
cal.addNonPTOCalendar(ptoId,userId,taskId,ws,we);

